script.socket-send:
function sendSocket(data) {
    if (socket && socket.readyState === WebSocket.OPEN) {
        socket.send(data);

        if (!irc_disable) {
            log(data, 'SOCK SEND');
        }
    }
    else {
        log(`Tried to send message, but socket is not open!`, 'SOCK SEND');
    }
}

