styles.body:
font-family: "Manrope", sans-serif;
background: #18181b;
color: #fff;
margin: 0;
padding: 0;
display: flex;
flex-direction: column;
height: 100vh;

styles.#status:
background: #9147ff;
padding: 10px;
text-align: center;
font-weight: bold;

styles..chat-container:
flex: 1;
display: flex;
flex-direction: column;
overflow-y: auto;
padding: 6px;
will-change: scroll-position;
padding-top: 65px;

styles..message:
margin: 2px 0px;
padding: 3px 5px;
transition: background-color 0.5s ease, border 0.5s ease;
overflow: hidden;
border: solid 1px #ffffff00;
border-radius: 10px;

styles..timestamp:
font-family: "Ubuntu Mono", monospace;
font-weight: 400;
color: #919191;
margin-right: 5px;
min-width: 50px;

styles..username:
font-family: "Manrope", sans-serif;
font-optical-sizing: auto;
font-weight: 700;
font-style: normal;
padding-bottom: 5px;

styles.#message-input:
flex: 1;
padding: 10px;
border: none;
background: #27272a60;
color: #fff;
font-size: 16px;
border-radius: 10px;
background: #ffffff05;

styles..badges:
display: inline-flex;
align-items: center;
vertical-align: middle;

styles..reply-reference:
margin: 5px 0 0 0;
padding-left: 10px;
border-left: 1px solid #444;
cursor: pointer;
font-style: italic;
color: #aaa;
transition: color 0.5s ease;

styles.#tab-bar:
display: flex;
position: fixed;
align-items: center;
background: #ffffff05;
padding: 10px;
padding-bottom: 5px;
margin: 8px 0px 0px 8px;
border-radius: 20px;
width: calc(100% - 36px);
backdrop-filter: blur(5px);
border: solid 1px #ffffff05;

styles.#tabs-container:
display: flex;
flex-grow: 1;
overflow-x: auto;
border-radius: 10px 10px 0px 0px;
margin-right: 8px;
padding-bottom: 5px;

styles..tab:
padding: 5px 10px;
margin-right: 5px;
background: #ffffff0D;
border-radius: 10px;
cursor: pointer;
white-space: nowrap;
border: solid 1px #ffffff05;
transition: 0.35s ease background, 0.35s ease border;
user-select: none;

styles.#add-tab-btn:
font-family: "Ubuntu Mono", monospace;
font-weight: 400;
align-content: center;
background: #ffffff15;
color: white;
font-size: 18px;
padding: 0 10px;
cursor: pointer;
border-radius: 10px;
height: 30px;
width: 30px;
border: solid 1px #ffffff10;
margin-bottom: 5px;

styles..message-content:
white-space: pre-wrap;
overflow-wrap: break-word;
hyphens: auto;
